import React from "react";
import ReactDOM from "react-dom";
import { SketchPicker } from "react-color";

function art() {

   
    return (
      <div className="about comp-space" id="art">
        
      </div>
    );
  }
  
  export default art;
  
class Sketch extends React.Component {
  state = {
    displayColorPicker: false
  };

  handleClick = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker });
  };

  handleClose = () => {
    this.setState({ displayColorPicker: false });
  };
  handleColorChange = (color) => {
    this.setState({
      color: color.hex,
      opa: color.rgb.a
    });
  };
  render() {
    console.log("color", this.state.color);
    console.log("opa", this.state.opa);
    const popover = {
      position: "absolute",
      zIndex: "2"
    };
    const cover = {
      position: "fixed",
      top: "0px",
      right: "0px",
      bottom: "0px",
      left: "0px"
    };
    return (
      <div>
        <button onClick={this.handleClick}>Pick Color</button>
        {this.state.displayColorPicker ? (
          <div style={popover}>
            <div style={cover} onClick={this.handleClose} />
            <SketchPicker
              color={this.state.color}
              onChange={this.handleColorChange}
              onSwatchHover={(color, e) => {
                console.log("color", color);
              }}
            />
          </div>
        ) : null}
        <div style={{ color: this.state.color }}>123213</div>

        <div style={{ color: this.state.color }}>
          <p>Blue</p>
        </div>
      </div>
    );
  }
}

const rootElement = document.getElementById("art");
ReactDOM.render(<Sketch />, rootElement);




